void swap( int ary[], int idx1, int idx2) {
    int tmp = ary[idx1];
    ary[idx1] = ary[idx2];
    ary[①] = tmp;
}
void sort( int ary[], int len ) {
    for( int i = 0; i < len; i++ ) {
        for( int j = 0; j < len - i - 1; j++ ) {
            if( ary[j] > ary[j+1]  ) {
                swap(ary, j, j+1);
            }
        }
    }
}
void main() {
    int ary[] = {15, 5, 20, 11, 8 };
    int nx = 5;    
    sort(ary, ②); 
    for(int i = 0; i < nx; i++) {      
        printf("%d \n", ary[i]);
    }
}

