struct ACC {
    int accNum;
    double bal;
};
void init(struct ACC *acc, int x, double y) {
    acc->accNum = x;
    acc->bal = y;
}
double sim(double base, int year) {
    double r = 1.0;
    for(int i = 0; i < year; i++) {
        r *= base;
    }
    return r;
}
void xxx(struct ACC *acc, double en) {
    if( en > 0 && en < acc->bal) {
        acc->bal -= en;
    }
    else {
        acc->bal += en;
    }
}
void yyy(struct ACC *acc) {
    acc->bal *= sim(1.1, 3);
}
int main() {
    struct ACC acc;
    init(&acc, 9981, 2200.0);
    xxx(&acc, 100.0);
    yyy(&acc);
    printf("%d and %0.2f \n", 
        acc.accNum, acc.bal);
    return 0;
}


