19. 다음 JAVA 프로그램의 출력결과를 쓰시오.

class Cal {
    int a;
    Cal (int a) {
        this.a = a;    
    }
    int func() {
        int b = 1;
        for( int i = 1; i < a; i++ )
            b = a * i + b;
        return a + b;
    }
}
public class Main {
    public static void main(String[] args) {
        Cal obj = new Cal(3);
        obj.a = 5;
        int b = obj.func();
        System.out.println(obj.a + b);
    }
}
