mapper.select:
/* com.sgg.login.mapper.LoginMapper.getId */
SELECT COUNT(*)
FROM SGG_USER
WHERE 1 = 1
AND USER_ID = #{userId}

/* com.sgg.login.mapper.LoginMapper.getId */
SELECT COUNT(*) AS CNT
FROM SGG_USER
WHERE 1 = 1
AND USER_ID = #{userId}
AND USER_PW = #{userPw}
AND DEL_YN = 'N'

mapper.insert:
/* com.sgg.login.mapper.LoginMapper.newAccount */
INSERT INTO SGG_USER
(	USER_ID
    , USER_PW
    , USER_NM
    , USER_AUTH
    , DEL_YN
    , LOGIN_CNT
)
VALUES
(	#{userId}
    , #{userPw}
    , #{userNm}
    , '001'
    , 'N'
    , 0
)

mapper.update:
/* com.sgg.login.mapper.LoginMapper.reAccount */
UPDATE SGG_USER
SET DEL_YN = 'N'
    , USER_AUTH = '001'
WHERE 1 = 1
AND USER_ID = #{userId}
AND USER_PW = #{userPw}
