mapper.update:
/* com.sgg.application.login.mapper.LoginMapper.resetLoginCount */
UPDATE SGG_USER
SET
    LOGIN_CNT = 0
WHERE 1 = 1
AND USER_ID = #{userId}

mapper.insert:
/* com.sgg.application.login.mapper.LoginMapper */
SELECT NVL(MAX(LOG_NO), 0) + 1 AS LOG_NO FROM SGG_LOG
INSERT INTO SGG_LOG ( LOG_NO , USER_ID , LOG_PATH , USER_IP , LOG_TIME , MENU_NAME ) VALUES ( #{logNo} , 'REACT' , 'REACT' , #{userIp} , SYSDATE , 'REACT' )
